import React, { Component } from 'react'

export class ApiTicker extends Component {
    constructor(props) {
        super(props)
        this.state = {
            ticker: null,
            price: null,
            volume: null,
            tickerInput: ''
        }
    }

    handleTickerChange = (e) => {
        this.setState({
            tickerInput: e.target.value
        })
    }

    handleSubmit = async (e) => {
        e.preventDefault();
        this.setState(await this.getTicker(this.state.tickerInput))
    }



    async getTicker(ticker) {
        const url = 'https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=' + ticker + '&apikey=TDYHB4DGZLP62GJE&datatype=json'
        const result = await fetch(url, {
            method: 'GET',
            headers: {
                'Accept' : 'application/json',
                'Content-Type':'application/json',
            }
        })
        const data = await result.json()
        return {
            ticker: data["Global Quote"]["01. symbol"],
            price: data["Global Quote"]["05. price"],
            volume: data["Global Quote"]["06. volume"],
            lastTradingDate: data["Global Quote"]["07. latest trading day"],
            changePercent: data["Global Quote"]["10. change percent"]
        }
    } 



    async componentDidMount() {

    }



    render() {
        const ticker = this.state.ticker
        const price = this.state.price
        const lastTradingDate =  this.state.lastTradingDate
        const volume = this.state.volume
        const changePercent = this.state.changePercent
        return (
            <div className="container">
                <h1> Current Ticker: {ticker} </h1>
                <h5> As of {lastTradingDate}, the price of  {ticker} was ${price}, at a change of {changePercent} </h5> 
                <h5> Volume traded on {lastTradingDate} was {volume} </h5> 


                <div>
                    <form onSubmit={this.handleSubmit}>
                        <label>
                            Ticker:
                            <input type="text"  value={this.state.tickerInput} onChange={this.handleTickerChange}/>
                        </label>
                        <input type="submit" value="submit" />
                    </form>
                </div>

             </div>
        )
    }
}

export default ApiTicker
