import React, { Component } from 'react'
import { newsAPI } from '../api'

export class NewsAPI extends Component {
    constructor(props) {
        super(props)

        this.state = {
             news: null
        }
    }

    handleClick = async () => {
        const data = await newsAPI(this.props.activeTicker)
        const parsed = data["articles"].slice(0,1)
        this.setState({
            news: parsed
        })
        console.log(parsed)
    }



    render() {

        const newsData = this.state.news

        const newsMap =  newsData.map((item) => {
            return <div> {item} </div>
        })

        return (
            <div>
                <button onClick={this.handleClick}> test news api </button> 
                    {newsMap}

            </div>
        )
    }
}

export default NewsAPI



