import React, { Component } from 'react'
import { dailyData } from '../api'
import { historicalDataThirty } from '../api'
import { historicalDataNinety} from '../api'
import { Line } from 'react-chartjs-2'

function newChartConfig(labels, data, activeTicker) {
    return {
        labels: labels, 
        datasets: [
          {
            label: activeTicker +  ' x ' + 'day graph',
            fill: false,
            lineTension: 0,
            backgroundColor: 'rgba(75,192,192,0.4)',
            borderColor: 'rgba(75,192,192,1)',
            borderCapStyle: 'butt',
            borderDash: [],
            borderDashOffset: 0.0,
            borderJoinStyle: 'miter',
            pointBorderColor: 'rgba(75,192,192,1)',
            pointBackgroundColor: '#fff',
            pointBorderWidth: 1,
            pointHoverRadius: 5,
            pointHoverBackgroundColor: 'rgba(75,192,192,1)',
            pointHoverBorderColor: 'rgba(220,220,220,1)',
            pointHoverBorderWidth: 2,
            pointRadius: 1,
            pointHitRadius: 10,
            data: data
          }
        ]
    }
}

export class Graph extends Component {
    constructor(props) {
        super(props)
        this.state = {
            graphedDays: null,
            dataPointsNeeded: null,
            xAxisData: null,
            data: null
        }
    }

    handleTestClick = async () => {
        const data = await historicalDataThirty(this.props.activeTicker)
        const parsed = data["Time Series (Daily)"]
        const xAxisData = Object.keys(parsed).map(e => e.slice(5, parsed.length)).splice(0, 30)
        console.log(xAxisData)
    }

    handleClickDaily = async () => {
        this.setState({
            graphedDays:1,
            dataPointsNeeded: 100 
        })
    }

    handleClickThirty = async () => {
        this.setState({
            graphedDays: 30,
            dataPointsNeeded: 90
        })
    }

    handleClickNinety = async () => {
        this.setState({
            graphedDays: 90 ,
            dataPointsNeeded: 90
        })
    }

    updateData = async () => {
        if (this.props.activeTicker === null) return // same as return null 
        if(this.state.graphedDays === 1 ) {
            const data = await dailyData(this.props.activeTicker)
            const parsed = data["Time Series (5min)"]
            const yAxisArr = Object.values(parsed).map((e) => +e["3. low"])
            const dailyDataArr = yAxisArr.splice(0, yAxisArr.length).reverse()
            const xAxisData = Object.keys(parsed).map(e => e.slice(0, 10)).splice(0,100)

            this.setState({
                data:dailyDataArr,
                xAxisData: xAxisData                
            })
        } else if (this.state.graphedDays === 30) {
            const data = await historicalDataThirty(this.props.activeTicker)
            const parsed = data["Time Series (Daily)"]
            const yAxisArr = Object.values(parsed).map((e) => +e["4. close"])
            const oneMonthDataPoints = yAxisArr.splice(0, 30).reverse()
            const xAxisData = Object.keys(parsed).map(e => e.slice(5, parsed.length)).splice(0, 30).reverse()

            this.setState({
                data: oneMonthDataPoints,
                xAxisData: xAxisData
            })
        } else if (this.state.graphedDays === 90) {
            const data = await historicalDataNinety(this.props.activeTicker)
            const parsed = data["Time Series (Daily)"]
            const yAxisArr = Object.values(parsed).map((e) => +e["4. close"])
            const threeMonthDataPoints = yAxisArr.splice(0, 90).reverse()
            const xAxisData = Object.keys(parsed).map(e => e.slice(5, parsed.length)).splice(0, 90).reverse()

            this.setState({
                data: threeMonthDataPoints,
                xAxisData:xAxisData
            })
        }
    }

    async componentDidMount(prevProps) {
        this.updateData()
    }

    async componentDidUpdate(prevProps, prevState) {
        if (prevProps.activeTicker !== this.props.activeTicker || 
            prevState.graphedDays !== this.state.graphedDays) {
            this.updateData()
        }
    }

    render() {

        const activeTicker = this.props.activeTicker
        const dataPoints = Array.from(Array(this.state.dataPointsNeeded).keys())


        const config = newChartConfig(this.state.xAxisData, this.state.data, activeTicker)

        return (
            <div>
                <button onClick={this.handleTestClick}>test</button>
                <button onClick={this.handleClickDaily}>1 day</button>
                <button onClick={this.handleClickThirty}> 30 day </button>
                <button onClick={this.handleClickNinety}> 90 day</button>

                <Line data={config} />
            </div>
        )
    }
}

export default Graph











