import React, { Component } from 'react'
import { newsAPI } from '../api'
import './components.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import Container  from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

export class NewsAPI extends Component {
    constructor(props) {
        super(props)
        this.state = { 
            newsList:[]
        }
    }


    async componentDidMount() {
        this.updateLinks()
    }

    updateLinks = async () => {
        if(this.props.activeTicker === null) return
        if(this.props.activeTicker) {
            const data = await newsAPI(this.props.activeTicker)
            console.log(data)
            const articleDict = {}
            data.articles.forEach((article) => {
                articleDict[article.urlToImage] = {
                    publisher: article.source.name, 
                    title: article.title,
                    url: article.url,
                    imgURL: article.urlToImage,
                    publishDate: article.publishedAt.slice(0, 10)
                }
            })

            const numbOfArticles = 10

            this.setState({
                newsList: Object.values(articleDict).slice(0, numbOfArticles)
            })
        }
    }

    async componentDidUpdate(prevProps) {
        if(prevProps.activeTicker !== this.props.activeTicker){
            this.updateLinks()
        }
    }


    render() {

        const newsList = this.state.newsList

        const newsListMap = newsList.map((item) => {

            const img = <img src={item.imgURL} height="50" width="50" style={{objectFit:"cover"}} />

            const columnOneItems =   
                                    <Container >
                                        <Container >   
                                            <Row id="news-publishersX">  {item.publisher}       </Row> 
                                            <Row id="news-publish-dateX">  {item.publishDate}   </Row> 
                                            <Row id="news-titleX">         {item.title}         </Row>
                                        </Container>
                                    </Container> 

            const columnTwoItems = <div> {img} </div>

            const format = 
                    <div className="test d-flex"> 
                        <a href={item.url} target ="_blank" key={item.url}>
                            <Row>  
                                <Col xl={10} lg={10} md={10} xs={10} className="float-left">
                                    {columnOneItems}
                                </Col>

                                <Col xl={2} lg={2} md={2} xs={2} className="float-right align-items-center">
                                    {columnTwoItems}
                                </Col>
                            </Row>
                        </a>
                    </div>    

            return format 
        })

        return (
            <div className="content-container" id="news">

            <h5> News </h5>

            <Container className="news-containerX"> 

                {newsListMap}

            </Container>

            </div>
        )
    }
}

export default NewsAPI



