import React, { Component } from 'react'
import TickerToggler from './TickerToggler'

class TickerList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            activeTicker: false,
            currentDisplay: 'price'
        }
    }

    render() {

        let tickerList = [
            {
            'companyName': 'Amazon',
            'ticker':'AMZN',
            'price': '$666',
            'volume' : '1,000,000,000',
            'percentChange': '+5%',
            active: false
            }, 
            {
            'companyName':'Facebook',
            'ticker':'FB',
            'price': '$420',
            'volume' : '123,456,789',
            'percentChange': '-99.99%',
            active: false
            },
            {
            'companyName': 'Tesla',
            'ticker':'TSLA',
            'price': '$69',
            'volume' : '420,696,666',
            'percentChange':'+420%',
            active: false
            }
        ]

        const tickerMapPrice = tickerList.map((item) => 
            <div> 
                <li key={item.ticker}> {item.ticker} {item.price} </li> 
                <TickerToggler />
            </div>
        )
        const tickerMapPercentChange = tickerList.map((item) => 
            <div> 
                <li key={item.ticker}> {item.ticker} {item.percentChange} </li> 
                <TickerToggler />
            </div>
        )



        if (this.state.currentDisplay === 'price') {
            return <div> {tickerMapPrice} </div>
        } else if (this.state.currentDisplay === 'percentChange')  {
            return <div> {tickerMapPercentChange} </div>
        }





        // return (
        //     <div>
        //         {tickerMap}
        //         <TickerToggler currentDisplay = {this.state.displayOptions} />
        //     </div>
        // )
    }
}

export default TickerList
