import React, { Component } from 'react'
import TickerToggler from './TickerToggler'

class TickerList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            activeTicker: false,
            currentDisplay: 'price'
        }
    }

    render() {

        let tickerList = [
            {
            'companyName': 'Amazon',
            'ticker':'AMZN',
            'price': '$666',
            'volume' : '1,000,000,000',
            'percentChange': '+5%',
            active: false
            }, 
            {
            'companyName':'Facebook',
            'ticker':'FB',
            'price': '$420',
            'volume' : '123,456,789',
            'percentChange': '-99.99%',
            active: false
            },
            {
            'companyName': 'Tesla',
            'ticker':'TSLA',
            'price': '$69',
            'volume' : '420,696,666',
            'percentChange':'+420%',
            active: false
            }
        ]


        const tickerMapPrice = tickerList.map((item) => 
            <li key={item.ticker}> {item.ticker} {item.price} </li> 
        )

        const tickerMapPercentChange = tickerList.map((item) => 
            <li key={item.ticker}> {item.ticker} {item.percentChange} </li> 
        )

        const tickerMapVolume = tickerList.map((item) => 
            <li key={item.ticker}> {item.ticker} {item.volume} </li> 
        )

        const menu =    <div> 
                            <label> Change Displayed Data </label>
                                <select value = {this.state.displayOptions} onChange ={this.handleDisplayChange}> 
                                    <option value="price">Price</option>
                                    <option value="percentChange">Percent Change</option>
                                    <option value="volume">Volume</option>
                                </select>
                        </div>

    handleDisplayChange = event => {
        console.log(event)
        this.setState({
            displayOptions: event.target.value
        })
    }


        if (this.state.currentDisplay === 'price') {
            return <div> 
                        {tickerMapPrice} 
                        {menu}
                    </div>

        } else if (this.state.currentDisplay === 'percentChange')  {
            return <div>    
                        {tickerMapPercentChange} 
                        {menu}
                    </div>

        } else if (this.state.currentDisplay === 'volume') {
            return <div>
                        {tickerMapVolume}
                        {menu}
                    </div>
        }
    }
}

export default TickerList
