import React, { Component } from 'react'
import { newsAPI } from '../api'
import { removeTypeDuplicates, numericLiteral } from '@babel/types'
import './components.css'

export class NewsAPI extends Component {
    constructor(props) {
        super(props)
        this.state = { 
            newsList:[]
        }
    }


    async componentDidMount() {
        this.updateLinks()
    }

    updateLinks = async () => {
        if(this.props.activeTicker === null) return
        if(this.props.activeTicker) {
            const data = await newsAPI(this.props.activeTicker)
            const articleDict = {}
            data.articles.forEach((article) => {
                articleDict[article.urlToImage] = {
                    title: article.title,
                    url: article.url,
                    imgURL: article.urlToImage,
                    publishDate: article.publishedAt.slice(0, 10)
                }
            })

            const numbOfArticles = 5

            this.setState({
                newsList: Object.values(articleDict).slice(0, numbOfArticles)
            })
        }
    }

    async componentDidUpdate(prevProps) {
        if(prevProps.activeTicker !== this.props.activeTicker){
            this.updateLinks()
        }
    }


    render() {

        const newsList = this.state.newsList

        const img = <img src={item.imgURL} />

        const newsListMap = newsList.map((item) => {
            return <a href={item.url} target="_blank" key ={item.url}>    
                         <li> {item.title} published on {item.publishDate} {img} </li>
                    </a>
        })


        return (
            <div className="content-container" id="news">

            <h5> News </h5>

                <ul>
                     {newsListMap}
                </ul>
            </div>
        )
    }
}

export default NewsAPI



