import React, { Component } from 'react'
import { overviewData } from "../api.js"
import './components.css'

export class FinancialRatios extends Component {
    constructor(props) {
        super(props)
        this.state = {
            currentDisplay: "showingActive", //showingActive || showingEditActive
            ratioList: {
                "MarketCapitalization": false,
                "EBITDA": false,
                "PERatio": true,
                "PEGRatio": true,
                "BookValue": false,
                "DividendPerShare": false,
                "DividendYield": false,
                "EPS": true,
                "RevenuePerShareTTM": false,
                "ProfitMargin": true,
                "OperatingMarginTTM": false,
                "ReturnOnAssetsTTM": false,
                "ReturnOnEquityTTM": false,
                "RevenueTTM": false,
                "GrossProfitTTM": false,
                "DilutedEPSTTM": false,
                "QuarterlyEarningsGrowthYOY": false,
                "QuarterlyRevenueGrowthYOY": false,
                "AnalystTargetPrice": false,
                "TrailingPE": false,
                "ForwardPE": false,
                "PriceToSalesRatioTTM": false,
                "PriceToBookRatio": false,
                "EVToRevenue": false,
                "EVToEBITDA": false,
                "Beta": false,
                "52WeekHigh": false,
                "52WeekLow": false,
                "50DayMovingAverage": false,
                "200DayMovingAverage": false,
                "SharesOutstanding": false,
                "SharesFloat": false,
                "SharesShort": false,
                "SharesShortPriorMonth": false,
                "ShortRatio": false,
                "ShortPercentOutstanding": false,
                "ShortPercentFloat": false,
                "PercentInsiders": false,
                "PercentInstitutions": false,
                "ForwardAnnualDividendRate": false,
                "ForwardAnnualDividendYield": false,
                "PayoutRatio": false
            },
            data: {}
        }
    }   

    onChange = (ratioKey) => {
        return () => {
            this.setState({ratioList: {...this.state.ratioList, [ratioKey]: !this.state.ratioList[ratioKey]}})
        }
    }


    async componentDidUpdate(prevProps) {
        if(prevProps.activeTicker !==  this.props.activeTicker) {
            this.setState({data: await overviewData(this.props.activeTicker)})
        }
    }

    showActive = () => {
        if(this.state.currentDisplay === "showingActive") return 
        else {
            this.setState({
                currentDisplay: "showingActive"
            })
        }
        console.log(this.state.currentDisplay)
    }

    showEditActive = () => {
        if(this.state.currentDisplay === "showingEditActive") return 
        else {
            this.setState({
                currentDisplay: "showingEditActive"
            })
        }
        console.log(this.state.currentDisplay)
    }


    render() {

        const currentDisplay = this.state.currentDisplay
        const checkedBoxes = []
        const notCheckedBoxes = []

        const currentDisplayFunc = () => {
            return currentDisplay === "showingActive" ? checkedBoxes : notCheckedBoxes 
        }


        for (const [key, value] of Object.entries(this.state.ratioList)){
            notCheckedBoxes.push(<div key={key}> 
                <label> 
                <input type="checkbox" value={value} onChange={this.onChange(key)} /> {key}  
                </label>  
            </div>) 

            // console.log(key, value)
            if(!value) continue 
            let dataValue = "N/A"
            if(this.state.data.hasOwnProperty(key)) {
                dataValue = this.state.data[key] 
            }
            checkedBoxes.push (  <div className="key-value-div"> 

                                <div className="ratio-type" key={key}> {key} </div> 
                                <div className="ratio-value"> {dataValue} </div> 

                                </div> )
        }

        const displayFormat = () => {
            return currentDisplayFunc() 
        }


        return (
            <div className="content-container" id="ratios">

                    <h5> Financial Ratios </h5> 

                    <div className="ratio-list-container">

                            {displayFormat()}

                    </div>

                <div className="change-ratio-display-btn-container d-flex justify-content-center">
                    <button id="show-checked-ratios" onClick={this.showActive} > Show Active </button>
                    <button id="show-edit-ratios" onClick={this.showEditActive}> Edit Active </button>
                </div>

            </div>
        )
    }
}

export default FinancialRatios


