import React, { Component } from 'react'
import TickerToggler from './TickerToggler'

class TickerList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            activeTicker: false
        }
    }

    render() {

        let tickerList = [
            {
            'companyName': 'Amazon',
            'ticker':'AMZN',
            'price': '$666',
            'percentChange': '+5%',
            active: false
            }, 
            {
            'companyName':'Facebook',
            'ticker':'FB',
            'price': '$420',
            'percentChange': '-99.99%',
            active: false
            },
            {
            'companyName': 'Tesla',
            'ticker':'TSLA',
            'price': '$69',
            'percentChange':'+420%',
            active: false
            }
        ]



        const tickerMap = tickerList.map((item) => 
        <li key={item.ticker}> {item.ticker} {item.price} </li> 
        )


        return (
            <div className="ticker-app-container">

                {tickerMap}

                <TickerToggler currentDisplay = {this.state.displayOptions} />
            </div>
        )
    }
}

export default TickerList
