import React, { Component } from 'react'

export class ApiTicker extends Component {
    constructor(props) {
        super(props)
        this.state = {
            data: null
        }
    }

    componentDidMount() {
        let ticker = 'amzn'.toUpperCase()
        // let url = 'https://jsonplaceholder.typicode.com/todos/1'
        const url = 'https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=' + ticker + '&apikey=TDYHB4DGZLP62GJE&datatype=json'
        fetch (url, {
            method: 'GET',
            headers: {
                'Accept' : 'application/json',
                'Content-Type':'application/json',
            }
        }).then((result) => {
            result.json().then((resp) => {
                this.setState({data: resp})
            })
        })
    }

    render() {
        const data = this.state.data
        if(data === null){
            return <div> loading... </div>
        } 
        console.log(data["Global Quote"])

        return (
            <div className="container">
                {
                    data ?
                    <div>
                        <h1> {data["Global Quote"]["01. symbol"]} </h1>
                        <h1> {data["Global Quote"]["05. price"]}</h1>
                    </div>
                    :
                    <p> please wait for api...</p>
                }
            </div>
        )
    }
}

export default ApiTicker
