import React, { Component } from 'react'
import { newsAPI } from '../api'
import { removeTypeDuplicates } from '@babel/types'

export class NewsAPI extends Component {
    constructor(props) {
        super(props)
        this.state = {
            newsList:[]
        }
    }


    async componentDidMount() {
        if(this.props.activeTicker === null) return
        if(this.props.activeTicker) {
            const data = await newsAPI(this.props.activeTicker)
            let newsList = []
            let finalList = []
            let j = 5 // number of news articles rendered 
            for(let i = 0; i < j; i++) {
                let article = {
                    title: data["articles"][i]["title"],
                    url: data["articles"][i]["url"],
                    imgURL: data["articles"][i]["urlToImage"],
                    publishDate: data["articles"][i]["publishedAt"].slice(0, 10)
                }
                newsList.push(article)
            }

            for(let i = 0; i < newsList.length; i++) {
                for(let j = i + 1; j < newsList.length; j++) {
                    let a1 = newsList[i]["imgURL"]
                    let a2 = newsList[j]["imgURL"] 
                    if( a1 !== a2 ){
                        finalList.push(newsList[i])
                    }
                }
            }



            this.setState({
                newsList: finalList
            })
        }
    }


    updateLinks = async () => {
        if(this.state.newsList === null) return
        if(this.props.activeTicker) {
            const data = await newsAPI(this.props.activeTicker)
            let newsList = []
            let j = 5 // number of news articles rendered 
            for(let i = 0; i < j; i++) {
                let article = {
                    title: data["articles"][i]["title"],
                    url: data["articles"][i]["url"],
                    imgURL: data["articles"][i]["urlToImage"],
                    publishDate: data["articles"][i]["publishedAt"].slice(0, 10)
                }
                newsList.push(article)
            }
            this.setState({
                newsList: newsList
            })
        }

    }

    async componentDidUpdate(prevProps) {
        if(prevProps.activeTicker !== this.props.activeTicker){
            this.updateLinks()
        }
    }


    render() {

        const newsList = this.state.newsList

        const newsListMap = newsList.map((item) => {
            return <a href={item.url} target="_blank" key ={item.url}>    
                        <li> {item.title} published on {item.publishDate} </li>
                    </a>
        })


        return (
            <div>
                <ul>
                     {newsListMap}
                </ul>
            </div>
        )
    }
}

export default NewsAPI



