import React, { Component } from 'react'
import { getTicker } from '../api.js'
import './components.css'

import 'bootstrap/dist/css/bootstrap.min.css';
import Container  from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'



class TickerList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            newTicker: '',
            activeTicker: null,
            currentDisplay: 'price',
            tickerList: [
                {
                'companyName': 'Amazon',
                'ticker': 'AMZN',
                'price': '$666',
                'volume' : '1,000,000,000',
                'changePercent': '+5%',
                }, 
                {
                'companyName':'Facebook',
                'ticker':'FB',
                'price': '$420',
                'volume' : '123,456,789',
                'changePercent': '-99.99%',
                },
                {
                'companyName': 'Tesla',
                'ticker':'TSLA',
                'price': '$69',
                'volume' : '420,696,666',
                'changePercent':'+420%',
                },
                {
                    'companyName': 'Amazon',
                    'ticker': 'AMZN',
                    'price': '$666',
                    'volume' : '1,000,000,000',
                    'changePercent': '+5%',
                    }, 
                    {
                    'companyName':'Facebook',
                    'ticker':'FB',
                    'price': '$420',
                    'volume' : '123,456,789',
                    'changePercent': '-99.99%',
                    },
                    {
                    'companyName': 'Tesla',
                    'ticker':'TSLA',
                    'price': '$69',
                    'volume' : '420,696,666',
                    'changePercent':'+420%',
                    },
                    {
                        'companyName': 'Amazon',
                        'ticker': 'AMZN',
                        'price': '$666',
                        'volume' : '1,000,000,000',
                        'changePercent': '+5%',
                        }, 
                        {
                        'companyName':'Facebook',
                        'ticker':'FB',
                        'price': '$420',
                        'volume' : '123,456,789',
                        'changePercent': '-99.99%',
                        },
                        {
                        'companyName': 'Tesla',
                        'ticker':'TSLA',
                        'price': '$69',
                        'volume' : '420,696,666',
                        'changePercent':'+420%',
                        },
                        {
                            'companyName': 'Amazon',
                            'ticker': 'AMZN',
                            'price': '$666',
                            'volume' : '1,000,000,000',
                            'changePercent': '+5%',
                            }, 
                            {
                            'companyName':'Facebook',
                            'ticker':'FB',
                            'price': '$420',
                            'volume' : '123,456,789',
                            'changePercent': '-99.99%',
                            },
                            {
                            'companyName': 'Tesla',
                            'ticker':'TSLA',
                            'price': '$69',
                            'volume' : '420,696,666',
                            'changePercent':'+420%',
                            },                {
                                'companyName': 'Amazon',
                                'ticker': 'AMZN',
                                'price': '$666',
                                'volume' : '1,000,000,000',
                                'changePercent': '+5%',
                                }, 
                                {
                                'companyName':'Facebook',
                                'ticker':'FB',
                                'price': '$420',
                                'volume' : '123,456,789',
                                'changePercent': '-99.99%',
                                },
                                {
                                'companyName': 'Tesla',
                                'ticker':'TSLA',
                                'price': '$69',
                                'volume' : '420,696,666',
                                'changePercent':'+420%',
                                }
            ]
        }
    }

    handleDisplayChange = e => {
        this.setState({
            currentDisplay: e.target.value
        })
    }


    onChange = (e) => {
       this.setState({
           newTicker: e.target.value
       })
    }

    onSubmit = async (e) => {
        e.preventDefault()
        const newestTicker = await getTicker(this.state.newTicker)
        if(newestTicker["ticker"] === undefined) {
            alert("Either you've entered an invalid ticker or the API is has reached it calls per minute limit...")
        } else {
                this.setState({tickerList: [...this.state.tickerList, newestTicker]})
        }

    }

    onClick = (ticker) => {
        return () => this.props.setActiveTicker(ticker)
    }

    render() {

        const tickerList = this.state.tickerList

        const tickerMap = tickerList.map((item) => {
            let value = item.price
            if(this.state.currentDisplay === "percentChange") value = item.changePercent
            return  <Container className="ticker-row">
                        <Row key={item.ticker} onClick={this.onClick(item.ticker)} > 
                            <Col > 
                                <span className="ticker-name float-left"> {item.ticker} </span> 
                            </Col>

                            <Col>
                                <span className="ticker-price float-right">  {value} </span> 
                            </Col>
                        </Row> 
                    </Container>
        })

        const addTickerForm =   <Container className="add-ticker-container ">
                                    <form onSubmit = {this.onSubmit}> 
                                        <input id="submit-form"type="text" value={this.state.newTicker} placeholder="Add Ticker..." autoComplete="off" onChange={this.onChange} required/> 
                                        <input id="submit-btn" type="submit" value="submit" />
                                    </form>
                                </Container>

        const toggles = <div className="ticker-btn-container float-right"> 
                            <button className="ticker-btns" value="price" onClick={this.handleDisplayChange}> Price </button>
                            <button className="ticker-btns" value="percentChange" onClick={this.handleDisplayChange}> Percent </button>
                        </div> 


        return <div className="content-container" id="ticker" >

                    <h5>Stocks</h5>

                    <div className ="ticker-map-container"> 
                        <Container>
                            {tickerMap} 
                        </Container>
                    </div>

                    <Container >
                        <Row> 
                            <Col> 
                                {addTickerForm}
                            </Col>

                            <Col> 
                                {toggles}
                            </Col>
                        </Row>
                    </Container>

                </div>
    }
}

export default TickerList
