import React, { Component } from 'react'


class TickerList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            activeTicker: null,
            currentDisplay: 'price',
            tickerList: [
                {
                'companyName': 'Amazon',
                'ticker':'AMZN',
                'price': '$666',
                'volume' : '1,000,000,000',
                'percentChange': '+5%',
                active: false
                }, 
                {
                'companyName':'Facebook',
                'ticker':'FB',
                'price': '$420',
                'volume' : '123,456,789',
                'percentChange': '-99.99%',
                active: false
                },
                {
                'companyName': 'Tesla',
                'ticker':'TSLA',
                'price': '$69',
                'volume' : '420,696,666',
                'percentChange':'+420%',
                active: false
                }
            ]
        }
    }

    handleDisplayChange = e => {
        console.log(e)
        this.setState({
            currentDisplay: e.target.value
        })
    }

    onChange = (e) => {
        // console.log(e.target.value)
    }


    render() {

        const tickerList = this.state.tickerList


        function addTicker(e) {
            e.preventDefault()
            tickerList.push(e)
            console.log(tickerList)
        }


        const tickerMapPrice = tickerList.map((item) => 
            <li key={item.ticker}> {item.ticker} {item.price} </li> 
        )

        const tickerMapPercentChange = tickerList.map((item) => 
            <li key={item.ticker}> {item.ticker} {item.percentChange} </li> 
        )

        const tickerMapVolume = tickerList.map((item) => 
            <li key={item.ticker}> {item.ticker} {item.volume} </li> 
        )

        const dataOptionMenu =  <div> 
                                    <label> Change Displayed Data </label>
                                        <select value = {this.state.currentDisplay} onChange ={this.handleDisplayChange}> 
                                            <option value="price">Price</option>
                                            <option value="percentChange">Percent Change</option>
                                            <option value="volume">Volume</option>
                                        </select>
                                </div>

        const addTickerForm =   <label> 
                                    <form onSubmit = {addTicker}> 
                                        <input type="text" placeholder="Add Ticker..." autoComplete="off" onChange={this.onChange} /> 
                                        <input type="submit" value="submit" />
                                    </form>
                                </label>

        if (this.state.currentDisplay === 'price') {
            return <div> 
                        {tickerMapPrice} 
                        {addTickerForm}
                        {dataOptionMenu}
                    </div>

        } else if (this.state.currentDisplay === 'percentChange')  {
            return <div>    
                        {tickerMapPercentChange}
                        {addTickerForm} 
                        {dataOptionMenu}
                    </div>

        } else if (this.state.currentDisplay === 'volume') {
            return <div>
                        {tickerMapVolume}
                        {addTickerForm}
                        {dataOptionMenu}
                    </div>
        }
    }
}

export default TickerList
