import React, { Component } from 'react'

export class ApiTicker extends Component {
    constructor(props) {
        super(props)
        this.state = {
            ticker: '',
            data: null
        }
    }

    handleTickerChange = (event) => {
        this.setState({
            ticker: event.target.value
        })
    }

    handleSubmit = (e) => {
        console.log(e.target.value)
        // this.setState({ticker: e.target.value})
    }


    componentDidMount() {

        let ticker = null
        const url = 'https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=' + ticker + '&apikey=TDYHB4DGZLP62GJE&datatype=json'
        fetch (url, {
            method: 'GET',
            headers: {
                'Accept' : 'application/json',
                'Content-Type':'application/json',
            }
        }).then((result) => {
            result.json().then((resp) => {
                this.setState({data: resp})
            })
        })
    }



    render() {
        const data = this.state.data

        if(data === null){
            return <div> loading... </div>
        } 


        return (
            <div className="container">
                <h1> hello {this.state.ticker} </h1>
                {
                    data ?
                    <div>
                        {/* <h1> {data["Global Quote"]["01. symbol"]} </h1>
                        <h1> {data["Global Quote"]["05. price"]}</h1> */}

                    </div>
                    :
                    <p> please wait for api...</p>
                }

                <div>
                    <form onSubmit={this.handleSubmit}>
                        <label>
                            Ticker:
                            <input type="text"  value={this.state.ticker} onChange={this.handleTickerChange}/>
                        </label>
                        <input type="input" value="submit" />
                    </form>
                </div>

             </div>
        )
    }
}

export default ApiTicker
