import React, { Component } from 'react'

class TickerToggler extends Component {
    constructor(props) {
        super(props)

        this.state = {
               displayOptions: 'price'
        }
    }

    handleDisplayChange = event => {
        console.log(event)
        this.setState({
            displayOptions: event.target.value
        })
    }

    render() {
        return (
            <div> 
                <label for="tickerOptions"> Change Displayed Data </label>
                    <select value = {this.state.displayOptions} onChange ={this.handleDisplayChange}> 
                        <option value="price">Price</option>
                        <option value="percentChange">Percent Change</option>
                        <option value="volume">Volume</option>
                    </select>
            </div>
        )
    }
}

export default TickerToggler
