import React, { Component } from 'react'
import { newsAPI } from '../api'

export class NewsAPI extends Component {
    constructor(props) {
        super(props)
        this.state = {
            newsList:[]
        }
    }



    async componentDidMount() {
        const data = await newsAPI(this.props.activeTicker)
        let newsList = []
        let j = 5 // number of news articles rendered 
        for(let i = 0; i < j; i++) {
            let article = {
                title: data["articles"][i]["title"],
                url: data["articles"][i]["url"],
                imgURL: data["articles"][i]["urlToImage"],
                publishDate: data["articles"][i]["publishedAt"].slice(0, 8)
            }
            newsList.push(article)
        }
        this.setState({
            newsList: newsList
        })
    }




    render() {

        const newsList = this.state.newsList

        const newsListMap = newsList.map((item) => {
            return <a href={item.url} target="_blank" classsName="news-links">    
                        <li key = {item.url} > {item.title} published at {item.publishDate} </li>
                    </a>
        })


        return (
            <div>
                <button onClick={this.handleClick}> test news api </button> 
                <ul>
                     {newsListMap}
                </ul>
            </div>
        )
    }
}

export default NewsAPI



