import React, { Component } from 'react'

export class ApiTicker extends Component {
    constructor(props) {
        super(props)
        this.state = {
            ticker: null,
            price: null,
            tickerInput: ''
        }
    }

    handleTickerChange = (event) => {
        this.setState({
            ticker: event.target.value
        })
    }

    async handleSubmit(e) {
        e.preventDefault();
        this.setState(await this.getTicker(this.state.tickerInput))
        // this.setState({ticker: e.target.value})
    }


    async getTicker(ticker) {
        const url = 'https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=' + ticker + '&apikey=TDYHB4DGZLP62GJE&datatype=json'
        const result = await fetch(url, {
            method: 'GET',
            headers: {
                'Accept' : 'application/json',
                'Content-Type':'application/json',
            }
        })
        const data = await result.json()
        return {
            ticker: data["Global Quote"]["01. symbol"],
            price: data["Global Quote"]["05. price"]
        }
    } 



    async componentDidMount() {

    }



    render() {
        if(this.state.ticker === null){
            return <div> loading... </div>
        } 

        return (
            <div className="container">
                <h1> hello {this.state.ticker} </h1>


                <div>
                    <form onSubmit={this.handleSubmit}>
                        <label>
                            Ticker:
                            <input type="text"  value={this.state.tickerInput} onChange={this.handleTickerChange}/>
                        </label>
                        <input type="submit" value="submit" />
                    </form>
                </div>

             </div>
        )
    }
}

export default ApiTicker
