import React, { Component } from 'react'
import { historicalData } from '../api'
import { Line } from 'react-chartjs-2'

export class Graph extends Component {
    constructor(props) {
        super(props)
        this.state = {
            graphedDays: 30,
            data: {}
        }
    }





    handleClick = async () => {
        const data = await historicalData(this.props.activeTicker)

        console.log(data)
    }





    render() {

        const activeTicker = this.props.activeTicker
        const graphDays = this.state.graphedDays

        const currentTicker = () => {
            if(activeTicker === null) {
                return "There is currently no active ticker"
            } else {
                return activeTicker + " 30 day dataset"
            }
        }




        const data = {
            labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
            datasets: [
              {
                label: currentTicker(),
                fill: false,
                lineTension: 0.1,
                backgroundColor: 'rgba(75,192,192,0.4)',
                borderColor: 'rgba(75,192,192,1)',
                borderCapStyle: 'butt',
                borderDash: [],
                borderDashOffset: 0.0,
                borderJoinStyle: 'miter',
                pointBorderColor: 'rgba(75,192,192,1)',
                pointBackgroundColor: '#fff',
                pointBorderWidth: 1,
                pointHoverRadius: 5,
                pointHoverBackgroundColor: 'rgba(75,192,192,1)',
                pointHoverBorderColor: 'rgba(220,220,220,1)',
                pointHoverBorderWidth: 2,
                pointRadius: 1,
                pointHitRadius: 10,
                data: [65, 59, 80, 81, 56, 55, 40]
              }
            ]
          };
        return (
            <div>
                <input type="button" onClick={this.handleClick}></input>

                <Line data={data} />
            </div>
        )
    }
}

export default Graph
