import React, { Component } from 'react'
import { getTicker } from '../api.js'


class TickerList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            newTicker: '',
            activeTicker: null,
            currentDisplay: 'price',
            tickerList: [
                {
                'companyName': 'Amazon',
                'ticker': 'amzn',
                'price': '$666',
                'volume' : '1,000,000,000',
                'changePercent': '+5%',
                active: false
                }, 
                {
                'companyName':'Facebook',
                'ticker':'FB',
                'price': '$420',
                'volume' : '123,456,789',
                'changePercent': '-99.99%',
                active: false
                },
                {
                'companyName': 'Tesla',
                'ticker':'TSLA',
                'price': '$69',
                'volume' : '420,696,666',
                'changePercent':'+420%',
                active: false
                }
            ]
        }
    }

    handleDisplayChange = e => {
        this.setState({
            currentDisplay: e.target.value
        })
    }

    onChange = (e) => {
       this.setState({
           newTicker: e.target.value
       })
    }

    onSubmit = async (e) => {
        e.preventDefault()
        const newestTicker = await getTicker(this.state.newTicker)
       if(newestTicker["ticker"] === undefined) {
           alert("Either you've entered an invalid ticker or the API is has reached it calls per minute limit...")
       } else {
            this.setState({tickerList: [...this.state.tickerList, newestTicker]})
       }
    }


    render() {

        const tickerList = this.state.tickerList

        const tickerMap = tickerList.map((item) => {
            const value = item.price
            if(this.state.currentDisplay === "percentChange") value = item.changePercent
            return  <li key={item.ticker}> {item.ticker} {value} </li> 
        })



        const dataFinancialMenu =  <div> 
                                    <label> Change Displayed Financial Data </label>
                                        <select value = {this.state.currentDisplay} onChange ={this.handleDisplayChange}> 
                                            <option value="price">Price</option>
                                            <option value="percentChange">Percent Change</option>
                                            <option value="volume">Volume</option>
                                        </select>
                                    </div>

        const addTickerForm =   <label> 
                                    <form onSubmit = {this.onSubmit}> 
                                        <input type="text" value={this.state.newTicker} laceholder="Add Ticker..." autoComplete="off" onChange={this.onChange} /> 
                                        <input type="submit" value="submit" />
                                    </form>
                                </label>

        return <div> 
                    {tickerMap} 
                    {addTickerForm}
                    {dataFinancialMenu}
                </div>
    }
}

export default TickerList
