import React, { Component } from 'react'
import TickerList from './components/TickerList'
import FinancialRatios from './components/FinancialRatios'
import Graph from './components/Graph'
import NewsAPI from './components/NewsAPI'

import 'bootstrap/dist/css/bootstrap.min.css';
import Container  from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

import { BrowserRouter as Router, Link, Route, Switch} from 'react-router-dom';


class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      activeTicker: 'AMZN'
    }
  }

  setActiveTicker = (ticker) => {
    this.setState({activeTicker: ticker})
  }


  render() {
    return (
      <div className="App">
        <Container fluid className="m-0">
          {/* COLUMN 1 */}
          <Row> 
            <Col xl={4} lg={6} md={12} xs={12} className="m-0">
                <Row className="m-0">
                    <Router> 
                      <Route component={TickerList} 
                                  activeTicker = {this.state.activeTicker}
                                  setActiveTicker = {this.setActiveTicker} 
                                  />
                    </Router>
                </Row>
                {/* ROW SPLIT */}
                <Row className="m-0"> 
                  <Graph activeTicker = {this.state.activeTicker} />
                </Row>
              </Col>
              {/* COLUMN 2 */}
              <Col xl={4} lg={6} md={12} xs={12} className="m-0">
                <FinancialRatios activeTicker = {this.state.activeTicker} />
              </Col>
              {/* COLUMN 3 */}
              <Col xl={4} lg={12} md={12} xs={12} className="m-0">
                <NewsAPI activeTicker = {this.state.activeTicker} />
              </Col>
            </Row>
        </Container>
      </div>
    )
  }
}



export default App;


