import React, { Component } from 'react'
import { dailyData } from '../api'
import { historicalDataThirty } from '../api'
import { historicalDataNinety} from '../api'
import { Line } from 'react-chartjs-2'

export class Graph extends Component {
    constructor(props) {
        super(props)
        this.state = {
            graphedDays: 30,
            data: {}
        }
    }





    handleClick = async () => {

    }

    handleClickDaily = async () => {
        this.setState({
            graphedDays:1
        })
    }

    handleClickThirty = async () => {
        this.setState({
            graphedDays: 30
        })
    }

    handleClickNinety = async () => {
        this.setState({
            graphedDays: 90
        })
    }




    render() {

        const activeTicker = this.props.activeTicker
        const graphedDays = this.state.graphedDays


        const currentTicker = () => {
            if(activeTicker === null) {
                return "There is currently no active ticker"
            } else {
                return activeTicker + " 30 day dataset"
            }
        }

        const test = () => {
            let testData = []
            for(let i = 0; i <= 30; i++) {
                testData.push(i)
            }
            return testData
        }

        const test2 = async () => {
            const data = await historicalDataThirty(this.props.activeTicker)
            const parsed = data["Time Series (Daily)"]
            const datesArr = Object.values(parsed).map((e) => +e["4. close"])
            const oneMonthDataPoints = datesArr.splice(0, 20).reverse()
            return oneMonthDataPoints
        }

        const pushNumberOfDays = () => {
            let labels = []
            if(graphedDays === 30) {
                for(let i = 1; i <= 30; i++){
                    labels.push(i.toString())
                } 
            } else {
                for(let i = 1; i <= 90; i++) {
                    labels.push(i.toString())
                }
            }
            return labels;
        }



        const data = {
            labels: pushNumberOfDays(),
            datasets: [
              {
                label: currentTicker(),
                fill: false,
                lineTension: 0.1,
                backgroundColor: 'rgba(75,192,192,0.4)',
                borderColor: 'rgba(75,192,192,1)',
                borderCapStyle: 'butt',
                borderDash: [],
                borderDashOffset: 0.0,
                borderJoinStyle: 'miter',
                pointBorderColor: 'rgba(75,192,192,1)',
                pointBackgroundColor: '#fff',
                pointBorderWidth: 1,
                pointHoverRadius: 5,
                pointHoverBackgroundColor: 'rgba(75,192,192,1)',
                pointHoverBorderColor: 'rgba(220,220,220,1)',
                pointHoverBorderWidth: 2,
                pointRadius: 1,
                pointHitRadius: 10,
                data: test2()
              }
            ]
          };

        return (
            <div>
                <button onClick={this.handleClick}>test</button>
                <button onClick={this.handleClickDaily}>1 day</button>
                <button onClick={this.handleClickThirty}> 30 day </button>
                <button onClick={this.handleClickNinety}> 90 day</button>

                <Line data={data} />
            </div>
        )
    }
}

export default Graph
