import React, { Component } from 'react'
import { dailyData } from '../api'
import { historicalDataThirty } from '../api'
import { historicalDataNinety} from '../api'
import { Line } from 'react-chartjs-2'

function newChartConfig(labels, data, activeTicker) {
    return {
        labels: labels,
        datasets: [
          {
            label: activeTicker +  ' x ' + 'day graph',
            fill: false,
            lineTension: 0.1,
            backgroundColor: 'rgba(75,192,192,0.4)',
            borderColor: 'rgba(75,192,192,1)',
            borderCapStyle: 'butt',
            borderDash: [],
            borderDashOffset: 0.0,
            borderJoinStyle: 'miter',
            pointBorderColor: 'rgba(75,192,192,1)',
            pointBackgroundColor: '#fff',
            pointBorderWidth: 1,
            pointHoverRadius: 5,
            pointHoverBackgroundColor: 'rgba(75,192,192,1)',
            pointHoverBorderColor: 'rgba(220,220,220,1)',
            pointHoverBorderWidth: 2,
            pointRadius: 1,
            pointHitRadius: 10,
            data: data
          }
        ]
    }
}

export class Graph extends Component {
    constructor(props) {
        super(props)
        this.state = {
            graphedDays: 30,
            data: null
        }
    }

    handleClick = async () => {
        const data = await historicalDataNinety(this.props.activeTicker)
        const parsed = data["Time Series (Daily)"]
        const datesArr = Object.values(parsed).map((e) => +e["4. close"])
        const threeMonthDataPoints = datesArr.splice(0, 90).reverse()
        console.log(threeMonthDataPoints)

    }

    handleClickDaily = async () => {
        this.setState({
            graphedDays:1
        })
    }

    handleClickThirty = async () => {
        this.setState({
            graphedDays: 30
        })
    }

    handleClickNinety = async () => {
        this.setState({
            graphedDays: 90
        })
    }

    updateData = async () => {
        if (this.props.activeTicker === null) return // same as return null 
        if(this.state.graphedDays === 1 ) {
            //call and set data to single day graph
        } else if ( this.state.graphedDays === 30) {
            const data = await historicalDataThirty(this.props.activeTicker)
            const parsed = data["Time Series (Daily)"]
            const datesArr = Object.values(parsed).map((e) => +e["4. close"])
            const oneMonthDataPoints = datesArr.splice(0, 30).reverse()
            this.setState({data: oneMonthDataPoints})
        } else if (this.state.graphedDays === 90) {
            const data = await historicalDataNinety(this.props.activeTicker)
            const parsed = data["Time Series (Daily)"]
            const datesArr = Object.values(parsed).map((e) => +e["4. close"])
            const threeMonthDataPoints = datesArr.splice(0, 90).reverse()
            this.setState({data: threeMonthDataPoints})
        }
    }

    async componentDidMount(prevProps) {
        this.updateData()
    }

    async componentDidUpdate(prevProps) {
        if (prevProps.activeTicker !== this.props.activeTicker) {
            this.updateData()
        }
    }

    render() {

        const activeTicker = this.props.activeTicker


        const currentTicker = () => {
            if(activeTicker === null) {
                return "There is currently no active ticker"
            } else {
                return activeTicker + " 30 day dataset"
            }
        }

        const config = newChartConfig(Array.from(Array(this.state.graphedDays).keys()), this.state.data, activeTicker)

        return (
            <div>
                {/* <h3>{currentTicker()}</h3> */}
                <button onClick={this.handleClick}>test</button>
                <button onClick={this.handleClickDaily}>1 day</button>
                <button onClick={this.handleClickThirty}> 30 day </button>
                <button onClick={this.handleClickNinety}> 90 day</button>

                <Line data={config} />
            </div>
        )
    }
}

export default Graph











