import React, { Component } from 'react'
import { getTicker } from '../api.js'


class TickerList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            newTicker: '',
            activeTicker: null,
            currentDisplay: 'price',
            tickerList: [
                {
                'companyName': 'Amazon',
                'ticker': 'amzn',
                'price': '$666',
                'volume' : '1,000,000,000',
                'changePercent': '+5%',
                active: false
                }, 
                {
                'companyName':'Facebook',
                'ticker':'FB',
                'price': '$420',
                'volume' : '123,456,789',
                'changePercent': '-99.99%',
                active: false
                },
                {
                'companyName': 'Tesla',
                'ticker':'TSLA',
                'price': '$69',
                'volume' : '420,696,666',
                'changePercent':'+420%',
                active: false
                }
            ]
        }
    }

    handleDisplayChange = e => {
        this.setState({
            currentDisplay: e.target.value
        })
    }

    onChange = (e) => {
       this.setState({
           newTicker: e.target.value
       })
    }

    onSubmit = async (e) => {
        e.preventDefault()
        const newestTicker = await getTicker(this.state.newTicker)
        if(newestTicker.length != 0 ) {
            return this.setState({tickerList: [...this.state.tickerList, newestTicker]})
        } else {
            return "you fucked up"
        }
    }


    render() {

        const tickerList = this.state.tickerList

        const tickerMapPrice = tickerList.map((item) => 
            <li key={item.ticker}> {item.ticker} {item.price} </li> 
        )

        const tickerMapPercentChange = tickerList.map((item) => 
            <li key={item.ticker}> {item.ticker} {item.changePercent} </li> 
        )

        const tickerMapVolume = tickerList.map((item) => 
            <li key={item.ticker}> {item.ticker} {item.volume} </li> 
        )

        const dataFinancialMenu =  <div> 
                                    <label> Change Displayed Financial Data </label>
                                        <select value = {this.state.currentDisplay} onChange ={this.handleDisplayChange}> 
                                            <option value="price">Price</option>
                                            <option value="percentChange">Percent Change</option>
                                            <option value="volume">Volume</option>
                                        </select>
                                    </div>

        const addTickerForm =   <label> 
                                    <form onSubmit = {this.onSubmit}> 
                                        <input type="text" value={this.state.newTicker} laceholder="Add Ticker..." autoComplete="off" onChange={this.onChange} /> 
                                        <input type="submit" value="submit" />
                                    </form>
                                </label>

        if (this.state.currentDisplay === 'price') {
            return <div> 
                        {tickerMapPrice} 
                        {addTickerForm}
                        {dataFinancialMenu}
                    </div>

        } else if (this.state.currentDisplay === 'percentChange')  {
            return <div>    
                        {tickerMapPercentChange}
                        {addTickerForm} 
                        {dataFinancialMenu}
                    </div>

        } else if (this.state.currentDisplay === 'volume') {
            return <div>
                        {tickerMapVolume}
                        {addTickerForm}
                        {dataFinancialMenu}
                    </div>
        }
    }
}

export default TickerList
